CREATE TABLE Cidade (id INT AUTO_INCREMENT NOT NULL, estado_id INT DEFAULT NULL, nome VARCHAR(35) NOT NULL, slug VARCHAR(35) NOT NULL, INDEX IDX_6D34366A9F5A440B (estado_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE Entidade (id INT AUTO_INCREMENT NOT NULL, nome VARCHAR(50) NOT NULL, endereco VARCHAR(50) NOT NULL, numero VARCHAR(10) NOT NULL, complemento VARCHAR(10) NOT NULL, cep VARCHAR(8) NOT NULL, bairro VARCHAR(50) NOT NULL, cidade VARCHAR(50) NOT NULL, estado VARCHAR(2) NOT NULL, telefone VARCHAR(50) NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE Estado (id INT AUTO_INCREMENT NOT NULL, nome VARCHAR(2) NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE Vaga (id INT AUTO_INCREMENT NOT NULL, nome VARCHAR(255) NOT NULL, descricao LONGTEXT NOT NULL, entidade VARCHAR(255) NOT NULL, email_template LONGTEXT NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE Voluntario (id INT AUTO_INCREMENT NOT NULL, nome VARCHAR(50) NOT NULL, email VARCHAR(50) NOT NULL, cidade VARCHAR(50) NOT NULL, estado VARCHAR(2) NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
ALTER TABLE Cidade ADD CONSTRAINT FK_6D34366A9F5A440B FOREIGN KEY (estado_id) REFERENCES Estado (id);
